	err = exec.QueryRowContext(ctx, cache.retQuery, nzUniqueCols...).Scan(returns...)
	if err != nil {
		return errors.Wrap(err, "model: unable to populate default values for mtb_job")
	}

CacheNoHooks:
	if !cached {
		mtbJobUpsertCacheMut.Lock()
		mtbJobUpsertCache[key] = cache
		mtbJobUpsertCacheMut.Unlock()
	}

	return o.doAfterUpsertHooks(ctx, exec)
