		if err = randomize.Struct(seed, x, dtbMemberDBTypes, false, strmangle.SetComplement(dtbMemberPrimaryKeyColumns, dtbMemberColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
